<?phpinclude_once 'app/app.php';GOB::$html=file_get_contents('tmpl/flat/base.html', true);$fget='home';if(isset($_GET['fget'])){$fget=$_GET['fget'];}if(isset($_POST['fget'])){$fget=$_POST['fget'];}$mod='nincs';if(isset($_GET['mod'])){$mod=$_GET['mod'];}if(isset($_POST['mod'])){$mod=$_POST['mod'];}function fget_becsatol($fget){    include_once 'app/base/'.$fget.'.php';    return ADT::$view;}switch ($mod) {    case 'login'://modulok becsatol치sa------------        if(GOB::$log_mod){$tartalom=MOD::login();}        else        {$tartalom=fget_becsatol($fget);}        break;    case 'contact':        $tartalom=MOD::email();        break;    default:  //file becsatol치s-----------        $tartalom=fget_becsatol($fget);}//lap gener치l치s a tartalommal-----------------------------------------GOB::$html= str_replace('<!--|tartalom|-->',$tartalom ,GOB::$html);GOB::$html=FeltoltS::mod(GOB::$html);GOB::$html=FeltoltS::from_LT(GOB::$html);GOB::$html=FeltoltS::LT_tisztit(GOB::$html);echo GOB::$html;